import React from "react";
import {
  ImageBackground,
  StyleSheet,
  Text,
  TextInput,
  View,
  Button,
} from "react-native";

import Background_Image from "../assets/login_page/background.png";

const Login_page = () => {
  const [email, setEmail] = React.useState("Enter your email");
  const [password, setPassword] = React.useState("Enter your password");

  return (
    <>
      <ImageBackground
        source={Background_Image}
        resizeMode="cover"
        style={styles.image}
      >
        <View style={styles.container}>
          <View style={styles.divider}></View>
          <Text style={styles.text}>Register?</Text>
          <TextInput
            style={styles.input}
            placeholder={email}
            placeholderTextColor={"white"}
          ></TextInput>
          <TextInput
            style={styles.input}
            placeholder={password}
            placeholderTextColor={"white"}
          ></TextInput>
          <Button title="Press Me" />
        </View>
      </ImageBackground>
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",

    width: 255,
  },
  image: {
    flex: 1,
    justifyContent: "center",
  },
  text: {
    color: "black",
    fontSize: 20,
    lineHeight: 25,
    alignSelf: "flex-end",
  },
  input: {
    backgroundColor: " rgba(255, 206, 69,1)",
    marginTop: 10,
    width: 255,
    height: 44,
  },
  divider: {
    height: "60%",
  },
});

export default Login_page;
