import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import React from "react";

const winWidth = Dimensions.get("window").width;
const snap = winWidth / 3 - 15;
const Carousel = (data) => {
  const [activePage, setActivePage] = React.useState(0);

  return (
    <View style={styles.container}>
      <ScrollView
        contentContainerStyle={styles.ScrollView}
        snapToInterval={snap}
        snapToStart={true}
        disableIntervalMomentum={true}
        horizontal={true}
        onScroll={function (event) {
          console.log(event.nativeEvent.contentOffset.x);
          setActivePage(Math.round(event.nativeEvent.contentOffset.x / snap));
        }}
      >
        {data.data.map((book, index) => {
          return (
            <TouchableOpacity
              key={index}
              style={index === activePage + 1 ? styles.activePage : styles.page}
            >
              <Image source={{ uri: book.thumbnail }} style={styles.image} />
            </TouchableOpacity>
          );
        })}
      </ScrollView>
    </View>
  );
};

export default Carousel;

const styles = StyleSheet.create({
  container: {
    margin: 15,
    width: winWidth - 30,
  },

  ScrollView: {
    height: 200,
    alignItems: "center",
  },
  page: {
    width: (winWidth - 30) / 3,
    height: 220,
    padding: 20,
  },
  image: {
    resizeMode: "contain",
    flex: 1,
  },

  activePage: {
    width: (winWidth - 30) / 3,
    paddingTop: 10,
    height: 220,
  },
});
